import UIKit

/// Protocol for login email presentor
protocol LoginEmailPresentationLogic {
    /// Present login Response
    ///
    /// - Parameters:
    ///   - response: API Response
    ///   - message: API Message
    ///   - success: API Success
    func presentLoginResponse(response: Login.ViewModel?, message: String, success: String) 
}

/// Class for login email presentor
class LoginEmailPresenter: LoginEmailPresentationLogic {
    /// Viewcontroller for login email
    weak var viewController: LoginEmailDisplayLogic?
    /// Present login Response
    ///
    /// - Parameters:
    ///   - response: API Response
    ///   - message: API Message
    ///   - success: API Success
    func presentLoginResponse(response: Login.ViewModel?, message: String, success: String) {
        self.viewController?.didReceiveLoginResponse(response: response, message: message, success: success)
    }
}
