    // SetUpLayout initial UI setup
    func setUpLayout() {
        // self.navigationItem.title = AlertMessage.loginTitle
        //GIDSignIn.sharedInstance().presentingViewController = self
        //GIDSignIn.sharedInstance()?.clientID = ServiceApiKey.Google.kClientID
